import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet-async';
import { db } from '../firebase';
import { collection, query, orderBy, onSnapshot, doc, updateDoc, deleteDoc, addDoc, serverTimestamp } from 'firebase/firestore';
import { LayoutDashboard, Users, BookOpen, Trophy, Settings, LogOut, CheckCircle, Clock, MessageSquare } from 'lucide-react';

const Admin = () => {
    const [activeTab, setActiveTab] = useState('bookings');
    const [bookings, setBookings] = useState([]);
    const [leads, setLeads] = useState([]);
    const [messages, setMessages] = useState([]);
    const [blogs, setBlogs] = useState([]);
    const [contests, setContests] = useState([]);
    const [newBlog, setNewBlog] = useState({ title: '', category: '', content: '', image: '', author: 'Admin' });
    const [newContest, setNewContest] = useState({ category: '', contest: '', prize: 'â‚¹10,000', icon: 'Microscope', color: '#009B77' });
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [password, setPassword] = useState('');

    const handleLogin = (e) => {
        e.preventDefault();
        if (password === 'LSEAdmin2024') {
            setIsAuthenticated(true);
        } else {
            alert('Incorrect Password');
        }
    };

    useEffect(() => {
        if (!isAuthenticated) return;

        // Fetch Counseling Bookings
        const qBookings = query(collection(db, "counseling_bookings"), orderBy("createdAt", "desc"));
        const unsubBookings = onSnapshot(qBookings, (snapshot) => {
            setBookings(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        });

        // Fetch Placement Leads
        const qLeads = query(collection(db, "placement_leads"), orderBy("createdAt", "desc"));
        const unsubLeads = onSnapshot(qLeads, (snapshot) => {
            setLeads(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        });

        // Fetch Blogs
        const qBlogs = query(collection(db, "blogs"), orderBy("createdAt", "desc"));
        const unsubBlogs = onSnapshot(qBlogs, (snapshot) => {
            setBlogs(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        });

        // Fetch Contests
        const qContests = query(collection(db, "competitions"), orderBy("createdAt", "desc"));
        const unsubContests = onSnapshot(qContests, (snapshot) => {
            setContests(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        });

        // Fetch Messages
        const qMessages = query(collection(db, "contact_messages"), orderBy("createdAt", "desc"));
        const unsubMessages = onSnapshot(qMessages, (snapshot) => {
            setMessages(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        });

        return () => {
            unsubBookings();
            unsubLeads();
            unsubBlogs();
            unsubContests();
            unsubMessages();
        };
    }, [isAuthenticated]);

    const handleAddBlog = async (e) => {
        e.preventDefault();
        try {
            await addDoc(collection(db, "blogs"), {
                ...newBlog,
                date: new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'short', year: 'numeric' }),
                createdAt: serverTimestamp()
            });
            setNewBlog({ title: '', category: '', content: '', image: '', author: 'Admin' });
            alert("Blog added successfully!");
        } catch (error) {
            console.error("Error adding blog:", error);
        }
    };

    const handleAddContest = async (e) => {
        e.preventDefault();
        try {
            await addDoc(collection(db, "competitions"), {
                ...newContest,
                createdAt: serverTimestamp()
            });
            setNewContest({ category: '', contest: '', prize: 'â‚¹10,000', icon: 'Microscope', color: '#009B77' });
            alert("Contest added successfully!");
        } catch (error) {
            console.error("Error adding contest:", error);
        }
    };

    const deleteItem = async (collectionName, id) => {
        if (window.confirm("Are you sure you want to delete this?")) {
            try {
                await deleteDoc(doc(db, collectionName, id));
            } catch (error) {
                console.error("Error deleting item:", error);
            }
        }
    };

    if (!isAuthenticated) {
        return (
            <div className="container" style={{ minHeight: '80vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <div style={{ background: 'white', padding: '3rem', borderRadius: '16px', boxShadow: '0 20px 40px rgba(0,0,0,0.1)', maxWidth: '400px', width: '100%' }}>
                    <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Admin Login</h2>
                    <form onSubmit={handleLogin}>
                        <div style={{ marginBottom: '1.5rem' }}>
                            <label style={{ display: 'block', marginBottom: '0.5rem' }}>Password</label>
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }}
                                placeholder="Enter admin password"
                            />
                        </div>
                        <button type="submit" className="btn btn-primary" style={{ width: '100%', justifyContent: 'center' }}>
                            Login to Dashboard
                        </button>
                    </form>
                </div>
            </div>
        );
    }

    return (
        <div style={{ display: 'flex', minHeight: '100vh', background: '#f8fafc' }}>
            <Helmet>
                <title>LSE Admin Panel</title>
            </Helmet>

            {/* Sidebar */}
            <div style={{ width: '280px', background: '#0f172a', color: 'white', padding: '2rem' }}>
                <div style={{ marginBottom: '3rem' }}>
                    <h2 style={{ fontSize: '1.25rem', fontWeight: 'bold' }}>LSE Admin</h2>
                    <p style={{ fontSize: '0.8rem', opacity: 0.6 }}>Management Console</p>
                </div>

                <nav style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                    <button
                        onClick={() => setActiveTab('bookings')}
                        style={{
                            display: 'flex', alignItems: 'center', gap: '1rem', padding: '0.75rem 1rem', borderRadius: '8px',
                            background: activeTab === 'bookings' ? 'rgba(255,255,255,0.1)' : 'transparent',
                            border: 'none', color: 'white', cursor: 'pointer', textAlign: 'left'
                        }}
                    >
                        <LayoutDashboard size={20} /> Dashboard
                    </button>
                    <button
                        onClick={() => setActiveTab('blogs')}
                        style={{
                            display: 'flex', alignItems: 'center', gap: '1rem', padding: '0.75rem 1rem', borderRadius: '8px',
                            background: activeTab === 'blogs' ? 'rgba(255,255,255,0.1)' : 'transparent',
                            border: 'none', color: 'white', cursor: 'pointer', textAlign: 'left'
                        }}
                    >
                        <BookOpen size={20} /> Blog Posts
                    </button>
                    <button
                        onClick={() => setActiveTab('contests')}
                        style={{
                            display: 'flex', alignItems: 'center', gap: '1rem', padding: '0.75rem 1rem', borderRadius: '8px',
                            background: activeTab === 'contests' ? 'rgba(255,255,255,0.1)' : 'transparent',
                            border: 'none', color: 'white', cursor: 'pointer', textAlign: 'left'
                        }}
                    >
                        <Trophy size={20} /> Competitions
                    </button>
                    <button
                        onClick={() => setActiveTab('messages')}
                        style={{
                            display: 'flex', alignItems: 'center', gap: '1rem', padding: '0.75rem 1rem', borderRadius: '8px',
                            background: activeTab === 'messages' ? 'rgba(255,255,255,0.1)' : 'transparent',
                            border: 'none', color: 'white', cursor: 'pointer', textAlign: 'left'
                        }}
                    >
                        <MessageSquare size={20} /> Messages
                    </button>
                </nav>

                <button
                    onClick={() => setIsAuthenticated(false)}
                    style={{ marginTop: 'auto', display: 'flex', alignItems: 'center', gap: '1rem', padding: '0.75rem 1rem', color: '#ef4444', border: 'none', background: 'transparent', cursor: 'pointer' }}
                >
                    <LogOut size={20} /> Logout
                </button>
            </div>

            {/* Main Content */}
            <div style={{ flex: 1, padding: '3rem' }}>
                {activeTab === 'bookings' && (
                    <div>
                        <h2 style={{ marginBottom: '2rem' }}>Lead Dashboard</h2>

                        {/* Summary Stats */}
                        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '1.5rem', marginBottom: '3rem' }}>
                            <div style={{ background: 'white', padding: '1.5rem', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
                                <p style={{ color: '#64748b', fontSize: '0.875rem' }}>Counseling Bookings</p>
                                <h3 style={{ fontSize: '2rem' }}>{bookings.length}</h3>
                            </div>
                            <div style={{ background: 'white', padding: '1.5rem', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
                                <p style={{ color: '#64748b', fontSize: '0.875rem' }}>Placement Leads</p>
                                <h3 style={{ fontSize: '2rem' }}>{leads.length}</h3>
                            </div>
                            <div style={{ background: 'white', padding: '1.5rem', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
                                <p style={{ color: '#64748b', fontSize: '0.875rem' }}>Total Leads</p>
                                <h3 style={{ fontSize: '2rem' }}>{bookings.length + leads.length}</h3>
                            </div>
                        </div>

                        {/* Counseling Table */}
                        <div style={{ background: 'white', padding: '2rem', borderRadius: '16px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)', marginBottom: '3rem' }}>
                            <h3 style={{ marginBottom: '1.5rem' }}>Counseling Sessions</h3>
                            <div style={{ overflowX: 'auto' }}>
                                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                                    <thead>
                                        <tr style={{ textAlign: 'left', borderBottom: '1px solid #e2e8f0', color: '#64748b' }}>
                                            <th style={{ padding: '1rem' }}>Name</th>
                                            <th style={{ padding: '1rem' }}>Topic</th>
                                            <th style={{ padding: '1rem' }}>Date</th>
                                            <th style={{ padding: '1rem' }}>Contact</th>
                                            <th style={{ padding: '1rem' }}>Status</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {bookings.map(book => (
                                            <tr key={book.id} style={{ borderBottom: '1px solid #f1f5f9' }}>
                                                <td style={{ padding: '1rem', fontWeight: '500' }}>{book.name}</td>
                                                <td style={{ padding: '1rem' }}>{book.topic}</td>
                                                <td style={{ padding: '1rem' }}>{book.date}</td>
                                                <td style={{ padding: '1rem' }}>{book.phone}</td>
                                                <td style={{ padding: '1rem' }}>
                                                    <span style={{
                                                        padding: '0.25rem 0.75rem', borderRadius: '50px', fontSize: '0.75rem',
                                                        background: book.status === 'confirmed' ? '#dcfce7' : '#fef3c7',
                                                        color: book.status === 'confirmed' ? '#166534' : '#92400e'
                                                    }}>
                                                        {book.status}
                                                    </span>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>

                        {/* Placement Leads Table */}
                        <div style={{ background: 'white', padding: '2rem', borderRadius: '16px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
                            <h3 style={{ marginBottom: '1.5rem' }}>Placement Interests</h3>
                            <div style={{ overflowX: 'auto' }}>
                                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                                    <thead>
                                        <tr style={{ textAlign: 'left', borderBottom: '1px solid #e2e8f0', color: '#64748b' }}>
                                            <th style={{ padding: '1rem' }}>Name</th>
                                            <th style={{ padding: '1rem' }}>Plan</th>
                                            <th style={{ padding: '1rem' }}>City</th>
                                            <th style={{ padding: '1rem' }}>Phone</th>
                                            <th style={{ padding: '1rem' }}>Status</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {leads.map(lead => (
                                            <tr key={lead.id} style={{ borderBottom: '1px solid #f1f5f9' }}>
                                                <td style={{ padding: '1rem', fontWeight: '500' }}>{lead.name}</td>
                                                <td style={{ padding: '1rem' }}>{lead.plan}</td>
                                                <td style={{ padding: '1rem' }}>{lead.city}</td>
                                                <td style={{ padding: '1rem' }}>{lead.phone}</td>
                                                <td style={{ padding: '1rem' }}>
                                                    <span style={{
                                                        padding: '0.25rem 0.75rem', borderRadius: '50px', fontSize: '0.75rem',
                                                        background: lead.status === 'confirmed' ? '#dcfce7' : '#fef3c7',
                                                        color: lead.status === 'confirmed' ? '#166534' : '#92400e'
                                                    }}>
                                                        {lead.status}
                                                    </span>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                )}

                {activeTab === 'blogs' && (
                    <div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
                            <h2>Manage Blog Posts</h2>
                        </div>

                        {/* Add Blog Form */}
                        <div style={{ background: 'white', padding: '2rem', borderRadius: '16px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)', marginBottom: '3rem' }}>
                            <h3 style={{ marginBottom: '1.5rem' }}>Add New Post</h3>
                            <form onSubmit={handleAddBlog} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem' }}>
                                <div style={{ gridColumn: 'span 2' }}>
                                    <label>Title</label>
                                    <input required style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newBlog.title} onChange={e => setNewBlog({ ...newBlog, title: e.target.value })} />
                                </div>
                                <div>
                                    <label>Category</label>
                                    <input required style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newBlog.category} onChange={e => setNewBlog({ ...newBlog, category: e.target.value })} />
                                </div>
                                <div>
                                    <label>Author Name</label>
                                    <input style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newBlog.author} onChange={e => setNewBlog({ ...newBlog, author: e.target.value })} />
                                </div>
                                <div>
                                    <label>Image URL (from Unsplash or Firebase Storage)</label>
                                    <input style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newBlog.image} onChange={e => setNewBlog({ ...newBlog, image: e.target.value })} />
                                </div>
                                <div style={{ gridColumn: 'span 2' }}>
                                    <label>Content (Full Blog Story)</label>
                                    <textarea required rows="6" style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newBlog.content} onChange={e => setNewBlog({ ...newBlog, content: e.target.value })}></textarea>
                                </div>
                                <button type="submit" className="btn btn-primary">Publish Blog Post</button>
                            </form>
                        </div>

                        {/* Blogs List */}
                        <div style={{ display: 'grid', gap: '1rem' }}>
                            {blogs.map(blog => (
                                <div key={blog.id} style={{ background: 'white', padding: '1.5rem', borderRadius: '12px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                    <div>
                                        <h4 style={{ margin: 0 }}>{blog.title}</h4>
                                        <span style={{ fontSize: '0.8rem', color: '#64748b' }}>{blog.category}</span>
                                    </div>
                                    <button onClick={() => deleteItem('blogs', blog.id)} style={{ color: '#ef4444', border: 'none', background: 'transparent', cursor: 'pointer' }}>Delete</button>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {activeTab === 'contests' && (
                    <div>
                        <h2 style={{ marginBottom: '2rem' }}>Manage Competitions</h2>

                        {/* Add Contest Form */}
                        <div style={{ background: 'white', padding: '2rem', borderRadius: '16px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)', marginBottom: '3rem' }}>
                            <h3 style={{ marginBottom: '1.5rem' }}>Add New Contest</h3>
                            <form onSubmit={handleAddContest} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem' }}>
                                <div style={{ gridColumn: 'span 2' }}>
                                    <label>Contest Name</label>
                                    <input required style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newContest.contest} onChange={e => setNewContest({ ...newContest, contest: e.target.value })} />
                                </div>
                                <div>
                                    <label>Category (e.g. Life Science, Biotech)</label>
                                    <input required style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newContest.category} onChange={e => setNewContest({ ...newContest, category: e.target.value })} />
                                </div>
                                <div>
                                    <label>Prize Amount</label>
                                    <input required style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newContest.prize} onChange={e => setNewContest({ ...newContest, prize: e.target.value })} />
                                </div>
                                <div>
                                    <label>Icon Name (e.g. Microscope, Microscope, Brain)</label>
                                    <input style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newContest.icon} onChange={e => setNewContest({ ...newContest, icon: e.target.value })} />
                                </div>
                                <div>
                                    <label>Theme Color (Hex code)</label>
                                    <input style={{ width: '100%', padding: '0.75rem', borderRadius: '8px', border: '1px solid #ddd' }} value={newContest.color} onChange={e => setNewContest({ ...newContest, color: e.target.value })} />
                                </div>
                                <button type="submit" className="btn btn-primary" style={{ gridColumn: 'span 2' }}>Add Contest</button>
                            </form>
                        </div>

                        {/* Contests List */}
                        <div style={{ display: 'grid', gap: '1rem' }}>
                            {contests.map(c => (
                                <div key={c.id} style={{ background: 'white', padding: '1.5rem', borderRadius: '12px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                    <div>
                                        <h4 style={{ margin: 0 }}>{c.contest}</h4>
                                        <span style={{ fontSize: '0.8rem', color: '#64748b' }}>{c.category} â€¢ {c.prize}</span>
                                    </div>
                                    <button onClick={() => deleteItem('competitions', c.id)} style={{ color: '#ef4444', border: 'none', background: 'transparent', cursor: 'pointer' }}>Delete</button>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {activeTab === 'messages' && (
                    <div>
                        <h2 style={{ marginBottom: '2rem' }}>Contact Messages</h2>
                        <div style={{ display: 'grid', gap: '1.5rem' }}>
                            {messages.map(msg => (
                                <div key={msg.id} style={{ background: 'white', padding: '2rem', borderRadius: '16px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
                                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
                                        <div>
                                            <h4 style={{ margin: 0 }}>{msg.name}</h4>
                                            <p style={{ margin: 0, fontSize: '0.875rem', color: '#64748b' }}>{msg.email}</p>
                                        </div>
                                        <button onClick={() => deleteItem('contact_messages', msg.id)} style={{ color: '#ef4444', border: 'none', background: 'transparent', cursor: 'pointer' }}>Delete</button>
                                    </div>
                                    <p style={{ background: '#f8fafc', padding: '1rem', borderRadius: '8px', margin: 0, fontSize: '1rem', lineHeight: '1.6' }}>
                                        {msg.message}
                                    </p>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Admin;

import React, { useState } from 'react';
import { Helmet } from 'react-helmet-async';
import { useNavigate } from 'react-router-dom';
import { Calendar, Clock, MessageSquare, User, Phone, Loader } from 'lucide-react';
import { db } from '../firebase';
import { collection, addDoc, serverTimestamp } from 'firebase/firestore';

const Counseling = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({ name: '', email: '', phone: '', date: '', topic: 'Career Guidance' });
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        try {
            // 1. Save to Firebase Firestore
            await addDoc(collection(db, "counseling_bookings"), {
                ...formData,
                status: 'pending',
                createdAt: serverTimestamp()
            });

            // 2. Send to Web3Forms (for email notification)
            const response = await fetch('https://api.web3forms.com/submit', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    access_key: 'aa3876b2-bc3a-43db-9896-f777584cbb14', // Provided by user
                    subject: `ðŸ”” New Counseling Booking - ${formData.name}`,
                    from_name: 'Life Science Engineering',
                    to: 'team.lifescienceengineering@gmail.com',
                    name: formData.name,
                    email: formData.email,
                    phone: formData.phone,
                    preferred_date: formData.date,
                    topic: formData.topic,
                    message: `New counseling session booking:\n\nName: ${formData.name}\nEmail: ${formData.email}\nPhone: ${formData.phone}\nPreferred Date: ${formData.date}\nTopic: ${formData.topic}`
                }),
            });

            const result = await response.json();

            // Redirect to confirmation page with booking data
            navigate('/booking-confirmation', {
                state: {
                    name: formData.name,
                    email: formData.email,
                    topic: formData.topic,
                    date: formData.date,
                    price: 'â‚¹499' // Standard Consultation Fee
                }
            });
        } catch (error) {
            console.error('Error:', error);
            // Still redirect on error or show message? User wants it to "come like this" so let's redirect.
            navigate('/booking-confirmation', {
                state: {
                    name: formData.name,
                    email: formData.email,
                    topic: formData.topic,
                    date: formData.date,
                    price: 'â‚¹499' // Standard Consultation Fee
                }
            });
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="container section">
            <Helmet>
                <title>Career Counseling | Book Expert Session</title>
                <meta name="description" content="Book 1-on-1 counseling sessions with industry experts for guidance on Life Science jobs, higher studies, and interview preparation." />
            </Helmet>
            <div className="text-center" style={{ marginBottom: '4rem', maxWidth: '800px', margin: '0 auto 4rem' }}>
                <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>Expert Career Counseling</h1>
                <p className="text-subtle" style={{ fontSize: '1.25rem' }}>
                    Get personalized guidance from industry experts to navigate your career in Life Sciences, Pharma, and Biotech.
                </p>
            </div>

            <div className="grid" style={{ gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', gap: '4rem', alignItems: 'start' }}>

                {/* Info Column */}
                <div>
                    <h2 style={{ marginBottom: '2rem' }}>What We Offer</h2>
                    <div style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>
                        <div style={{ display: 'flex', gap: '1rem' }}>
                            <div style={{ width: 48, height: 48, background: '#e0f2fe', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'var(--color-primary)' }}>
                                <User size={24} />
                            </div>
