                    onMouseLeave={(e) => e.currentTarget.style.background = 'white'}
                >
                    <ChevronLeft size={24} color="#64748b" />
                </button>

                <button
                    onClick={goToNext}
                    style={{
                        position: 'absolute',
                        right: '-20px',
                        top: '50%',
                        transform: 'translateY(-50%)',
                        width: '48px',
                        height: '48px',
                        borderRadius: '50%',
                        background: 'white',
                        border: '1px solid #e2e8f0',
                        boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
