                {/* Testimonial Cards */}
                <div className="grid grid-cols-3" style={{ gap: '2rem', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))' }}>
                    {visibleTestimonials.map((testimonial, index) => (
