                                background: 'white',
                                borderRadius: '20px',
                                padding: '2rem',
                                border: '1px solid #e2e8f0',
                                boxShadow: '0 10px 30px -10px rgba(0,0,0,0.1)',
                                position: 'relative',
                                transition: 'all 0.5s ease',
                                display: 'flex',
                                flexDirection: 'column'
